    public static int FlipForBalance(string s)
    {
        int balance = 0;
        int flips = 0;

        foreach (char c in s)
        {
            if (c == '(')
            {
                balance++;
            }
            else        if (c == ')')
            {
                balance--;
            }

            if        (balance < 0)
            {
                flips++;
                balance        = 1; // Flip ')' to '('
            }
        }

        flips        += balance / 2; // Each pair of unmatched '(' can be flipped

        return        flips;
    }
